import axios from "axios";

export const add = (data) => {
  return (dispatch) => {
    const user = JSON.parse(localStorage.getItem("user"));
    const token = user.token;
    axios
      .post("https://api.riyofirsan.com/post/create", data, {
        headers: { "token": token },
      })
      .then((response) => {
        console.log(response);
        dispatch({
          type: "POST_ADD",
          payload: response.data,
        });
      });
  };
};

export const getData = () => {
  return (dispatch) => {
    const user = JSON.parse(localStorage.getItem("user"));
    const token = user.token;
    axios
      .get("http://localhost:8000/post/show", {
        headers: { "token": token },
      })
      .then((response) => {
        console.log(response.data);

          dispatch({
          type: "POST_SHOW",
          payload: response.data
        });
      });
  };
};

export const addLike = (targetPostId) => {
  return dispatch => {
    const user = JSON.parse(localStorage.getItem("user"))
      const token = user.token
      window.alert("addLike")

      axios.post("https://api.riyofirsan.com/like/create",{targetPostId},{
        headers: { "token": token },
      })
      .then(response => {
        dispatch({
          type: "POST_ADD_LIKE",
          payload: response.data
        })

      })
      .catch(err => err)
  }
}

export const user = () => {
  const user = JSON.parse(localStorage.getItem('user'))
const id = user.id
  return (dispatch) => {
    axios.get(`https://api.riyofirsan.com/users/show/${id}`)
    .then((response) => {
      console.log(response.data)
    .catch(err =>{
      window.alert(err)
    })
    dispatch({
      type : "USER",
      payload : response.data
    })
    })
  }
}
