import React, {useState, useEffect} from 'react'
import axios from "axios"

import "./Add.css";

const CommentPost = (props) => {

    const {data} = props

    const [comment,setComment] = useState([])

    const url = process.env.REACT_APP_API_URL



    useEffect(() =>{

        async function myComment() {
            const user = JSON.parse(localStorage.getItem("user"))
            const token = user.token
            const result = await axios.get(`${url}/comment/find/${props.data._id}`,{
            headers: { "token": token }
            }).catch(err => {
                window.alert("error",err)
            })
            setComment(result.data)
            console.log(result.data)
        }
        myComment()

    }, [])


    let getComment = comment.map((item,index) => {
        console.log(item.comment)
        return(
            <div key={item} data={index}>
                <div className="card mt-3 mb-3">
                    <div className="card-header">
                     {item.comment.map((item,index) => <h6 key={index} data={item}>{item.userComment.name}</h6>)}
                    </div>
                    <div className="card-body">
                    {item.comment.map((item,index) => <h6 key={index} data={item}>{item.commentText}</h6>)}
                    </div>
                </div>
            </div>
        )
        // let reduce = item.comment.map((item,index)=>{
        //     console.log(item.userComment)
        //     return (
        //         <div key={item} data={index}>
        //             <p>{item.userComment}</p>
        //         </div>
        //     )
        // })
        // return [reduce]
    })    

    return (
        <div>{getComment}</div>
    )

};

export default CommentPost
