import React, {useState, useEffect} from 'react'
import {Formik, Form} from 'formik'
import "./Add.css";
import {connect} from "react-redux";
import {getcomment} from "../../actioncreators/comment"
import {Modal,Button} from "react-bootstrap"
import axios from "axios"


const GetComment = (props) => {

    const {data} = props

    const url = process.env.REACT_APP_API_URL

    useEffect(()=>{

        const user = JSON.parse(localStorage.getItem("user"));
        const token = user.token;
        axios
          .get(`${url}/comment/show/`, {
            headers: { "token": token },
          })
          .then((response) => {
              response.data = data
            return data
          })

    },[])


    let showComment = data.map((item,index)=>{
        console.log(item.comment.commentUser)
        return(
            <div key={index}>
                <div>
                    {item.comment.map((item , index) => 
                    <div key={index}>
                        <h1>{item.commentUser}</h1>
                    </div>
                    )}
                </div>
            </div>
        )
    })

    return(
    <div>{showComment}</div>
    )

}



const mapStateToProps = (state) => {
    return {
        data: state.commentUser.data
    }
}

const mapDispatchToProps = {
    getcomment: getcomment,

};

export default connect(mapStateToProps, mapDispatchToProps)(GetComment);
