import React, { useState } from 'react'
import {connect} from 'react-redux'
import {filterSearch} from '../../actioncreators/search'
import searchFilter from '../../reducers/search';

const navbarSearch = props => {
    const [searchInput, setSearchInput] = useState("");

    const handleChange = (event) => {
      setSearchInput(event.target.value);
    };

    const handleKeyUp = (event) => {
        search()
    }

    const search = () => {
      props.filterSearch(searchInput);
    };

    return(
        <form className="form-inline my-2 my-lg-0 ">
        <div className="input-group">
          <input
            type="text"
            className="form-control"
            placeholder="Search"
            size="50"
            onChange={handleChange}
            onKeyUp={handleKeyUp}
          />
          <button
            class="btn btn-info"
            onClick={search}
            style={{ backgroundColor: "#8D7B65" }}
          >
            <FontAwesomeIcon icon={faSearch} />
          </button>
        </div>
      </form>
    )
}

const mapDispatchToProps = {
    filterSearch:filterSearch,
  };

  export default connect(null, mapDispatchToProps)(navbarSearch);
