import React, {useState,useEffect} from 'react'
import Navbar from '../Navbar'
import {Formik,Form} from 'formik'

import { connect } from "react-redux";
import './Addpage.css'
import {editProfile} from '../../actioncreators/Home'
import Thumb from "./Thumbs"
import Dropzone from "react-drop-zone"
import * as yup from 'yup';

const Add = (props) =>{

    const dropzoneStyle = {
        width: "100%",
        height: "auto",
        borderWidth: 2,
        borderColor: "rgb(102, 102, 102)",
        borderStyle: "dashed",
        borderRadius: 5,
      }

    return (
        <div className="yourStyle">
      <div className="card p-3 rounded" style={{ width: "30rem", backgroundColor : "white"}}>
        {/* <img
          src={Logo}
          className="mx-auto"
          alt="..."
          style={{ width: "55px" }}
        /> */}
        <h4 className="card-title mx-auto">want to change your style</h4>
        <Formik

            initialValues={{
                name : "",
                about : "",
                image : null,
                backGroundImage : null
            }}



            onSubmit={(values) =>{
                let formData = new FormData()

                formData.set("name",values.name)
                formData.set("about",values.about)
                formData.append("image",values.image)
                formData.append("image",values.backGroundImage)

                props.editProfile(values)
            }}

            validationSchema={yup.object().shape({
                recaptcha: yup.array(),
              })}
        >{(props) =>(
            <Form onSubmit={props.handleSubmit}>
                <div className="form-group">
            <label>Email address</label>
            <input
              type="text"
              className="form-control"
              id="name"
              name="name"
              onChange={props.handleChange}
              value={props.values.name}
            />
          </div>
          <div className="form-group">
            <label>Password</label>
            <input
              type="text-area"
              className="form-control"
              id="about"
              name="about"
              onChange={props.handleChange}
              value={props.values.about}
            />
          </div>
          <div className="form-group">
            <label>Multiple files</label>
            <Dropzone style={dropzoneStyle} accept="image/*" onDrop={(acceptedFiles) => {

              if (acceptedFiles.length === 0) { return; }


              props.setFieldValue("images", props.values.image.concat(acceptedFiles));
            }}>
              {({ isDragActive, isDragReject, acceptedFiles, rejectedFiles }) => {
                if (isDragActive) {
                  return "This file is authorized";
                }

                if (isDragReject) {
                  return "This file is not authorized";
                }

                if (props.image === undefined) { 
                  return <p>Try dragging a file here!</p>
                }

                return props.image.map((file, i) => (<Thumb key={i} file={file} />));
              }}
            </Dropzone>
          </div>

          {/* <p className="text-center leading">
            Don't have an account ? <Link to="/register">Register</Link>
          </p> */}
            <button type="submit" className="btn text-light btn-block">
              Submit
            </button>
            </Form>
        )}

        </Formik>
      </div>
    </div>
    )

}

const mapDispatchToProps = {editProfile : editProfile}

export default connect(null, mapDispatchToProps)(Add)
