import React, { useState, useEffect } from "react";
import axios from "axios";
import "./Add.css";

const CommentPost = (props) => {
  const { data } = props;

  const [comment, setComment] = useState([]);

  const url = process.env.REACT_APP_API_URL;

  // useEffect(() => {
  //   async function myComment() {
  //     const user = JSON.parse(localStorage.getItem("user"));
  //     const token = user.token;
  //     const result = await axios
  //       .get(`${url}/comment/find/${props.data._id}`, {
  //         headers: { token: token },
  //       })
  //       .catch((err) => {
  //         window.alert("error", err);
  //       });
  //     setComment(result.data);
  //   }
  //   myComment();
  // }, []);

  useEffect(() => {
    if (props.comments) {
      if (props.comments.targetPostId == data._id) {
        async function myComment() {
          const user = JSON.parse(localStorage.getItem("user"));
          const token = user.token;
          const result = await axios.get(`${url}/comment/find/${props.data_id}`, {
            headers: { token: token },
          })
          .catch( err => {
            window.alert("error",err)
          })
          setComment(result.data);
        }
        myComment()
      }
    }
  });

  let getComment = comment.map((item, index) => {
    return (
      <div key={index}>
        {item.comment.map((item, index) => (
          <ul key={index} className="list-group list-group-flush">
            <li className="list-group-item border-top">
              <div className="d-inline-flex flex-row">
                <img
                  src={`${url}/${item.userComment.image}`}
                  style={{
                    height: "25px",
                    width: "25px",
                    borderRadius: "50%",
                  }}
                />
                <p className="lead ml-1 mb-1">{item.userComment.name}</p>
              </div>
              <p className="card-text">{item.commentText}</p>
            </li>
          </ul>
        ))}
      </div>
    );
  });

  return (
    <div>
      <div>{getComment}</div>
    </div>
  );
};

export default CommentPost;
