import {Formik} from 'formik'
import "./Add.css";
import { connect } from "react-redux";
import { add } from "../../actioncreators/Home";
import {Form} from 'react-bootstrap'
import Tag from './Tag'


const Add = (props) => {

  const user = JSON.parse(localStorage.getItem("user"));
  const id = user.id;  

  return (
      <Formik
        initialValues={{
          name : id,
          description : '',
          image : null,
          tags : []
        }}
        onSubmit ={(values)=>{
