import React, { useState } from "react";
import "./Style.css"
import {connect} from "react-redux"
import Attach from "../Img/paperclip.svg"

const Add = (props) => {
  const [data, setData] = useState({
    number: 0,
    status: false,
    title: "",
    year: 1990,
  });

  const handleAdd = () => {
    props.add(data);
  };

  const handleClose = () => {
    props.hideAdd();
  };

  const handleChange = (event) => {
    let { name, value, type, checked } = event.currentTarget;
    if (type == "checkbox") {
      setData({
        ...data,
        [name]: checked,
      });
      console.log(checked);
    } else {
      setData({
        ...data,
        [name]: value,
      });
    }
  };

  return (
    <div className="container">
      <div className="form-group">
        <textarea
          className="form-control mt-5"
          id="add"
          rows={6}
          defaultValue={""}
          placeholder="Type something...."
          onChange={handleChange}
        />
        <img src={Attach} style={ {width : "15px", marginBottom : "10px"}}/>
      </div>

      <br/>
      <button type="button" class="btn btn-info" onClick={handleAdd} >Submit</button>
    </div>
  );
};

const mapDispatchToProps = {Add};

export default connect(null, mapDispatchToProps)(Add);
