import axios from "axios";

export const filterSearch = name=> {
    return ( dispatch => {
        let params = {};
        if (name){
            params = {
                name
            }
        }
        axios.get(`http://api.riyofirsan.com/users/findQuery`,{params})

        .then( (response) => {
            dispatch({
                type: 'SEARCH_LIST',
                payload: response.data
            })
        })
    })
}
