    const location = ((resolve, reject) => {
      console.log("halo");
      if (!geolocation) {
        reject(new Error("NOT SUPORTED"));
      }

      geolocation.getCurrentPosition(
        (position) => {
          console.log("duwa");
          disptach( {
            type: GET_LOCATION,
            payload: location,
          });
        },
        () => {
          reject(new Error("permision denined"));
