import axios from "axios";

export const add = (data) => {
  return (dispatch) => {
    axios
      .post(
        "https://eyedentity-socialmedia.herokuapp.com/post/create",{header: {"token" : localStorage.getItem(token)}},data
      )
      .then((response) => {
        dispatch({
          type: "POST_ADD",
          payload: response.data,
        });
      });
  };
};
