const initialState = {
  data: [],
  image : null
};

const homeUser = (state = initialState, action) => {
  switch (action.type) {
    case "POST_ADD":
      return { ...state, data: [...state.data, action.payload] };
    case "POST_SHOW":
      return { ...state, data: action.payload };
    case "ADD_IMAGE" :
      return {...state , image : action.payload}

    default:
      return state;
  }
};

export default homeUser;
