import React, {useState, useEffect} from 'react'
import { connect } from "react-redux";
import {getlocation} from '../../actioncreators/GetLocation'
import Geocode from "react-geocode";




const Location = (props) => {

  const {coords:{latitude ,longitude}} = props



  useEffect(()=>{
    props.getlocation()
  },[])



    Geocode.setApiKey('AIzaSyAVDqkzOARvGHvFnfaIYEiDBeIFaTjDDGI')
    const {address,setaddres} = useState({})
    Geocode.fromLatLng(`${latitude}`,`${longitude}`)
    .then(
    response => {
    response.results[0].formatted_address = address

    },
    error => {
      console.error(error);
    },

    )



  return (
    <div>
      <div>Latitude: <span>{latitude}</span></div>
        <div>Longitude: <span>{longitude}</span></div>
        <div>address :{address}</div>

    </div>
  )
}

const mapStateToProps = (state) =>{
  return {
    coords : state.location.coords
  }
}

const mapDispatchToProps = {
  getlocation : getlocation
}

export default connect(mapStateToProps,mapDispatchToProps)(Location)
