import React from "react";
import { Formik, Form } from "formik";

import { connect } from "react-redux";
import "./Addpage.css";
import { editProfile } from "../../actioncreators/Home";
import { addLike } from "../..//actioncreators/Home";
import { Link } from "react-router-dom";
import {
  ModalTitle,
  ModalBody,
  ModalFooter,
  Modal,
  Button,
} from "react-bootstrap";

const Edit = (props) => {
  const [showModal, setShowModal] = React.useState(false);
  return (
    <Modal
      {...props}
      size="md"
      aria-labelledby="contained-modal-title-vcenter"
      centered
      onSubmit={() => setShowModal(false)}  
    >
      <Modal.Header closeButton>
        <Modal.Title id="contained-modal-title-vcenter">
          Modal heading
        </Modal.Title>
      </Modal.Header>
      <Modal.Body>
        <Formik
          initialValues={{
            name: "",
            about: "",
            image: null,
            backGroundImage: null,
          }}
          onSubmit={(values) => {
            let formData = new FormData();

            formData.set("name", values.name);
            formData.set("about", values.about);
            formData.append("image", values.image);
            formData.append("backGroundImage", values.backGroundImage);

            props.editProfile(formData);
          }}
        >
          {(props) => (
            <Form onSubmit={props.handleSubmit}>
              <div className="form-group">
                <label>Name</label>
                <input
                  type="text"
                  className="form-control"
                  id="name"
                  name="name"
                  onChange={props.handleChange}
                  value={props.values.name}
                />
              </div>
              <div className="form-group">
                <label>Status Message</label>
                <input
                  type="text-area"
                  className="form-control"
                  id="about"
                  name="about"
                  onChange={props.handleChange}
                  value={props.values.about}
                />
              </div>
              <div className="form-group">
                <label>Photo Profile</label>
                <input
                  type="file"
                  className="form-control"
                  id="image"
                  name="image"
                  onChange={(event) => {
                    props.setFieldValue("image", event.currentTarget.files[0]);
                  }}
                />
              </div>
              <div className="form-group">
                <label>Profile Background </label>
                <input
                  type="file"
                  className="form-control"
                  id="image"
                  name="backGroundImage"
                  onChange={(event) => {
                    props.setFieldValue(
                      "backGroundImage",
                      event.currentTarget.files[0]
                    );
                  }}
                />
              </div>
              <div className="settingsave">
                <button
                  type="submit"
                  className="btn text-light btn-block"

                >
                  Save Changes
                </button>
              </div>
            </Form>
          )}
        </Formik>
      </Modal.Body>
    </Modal>
  );
};



const mapDispatchToProps = { editProfile: editProfile };

export default connect(null, mapDispatchToProps)(Edit);
