import axios from "axios";

const token = JSON.parse(localStorage.getItem('user',{token:"token"}))
console.log(token)

export const add = (data) => {
  return (dispatch) => {
    axios
      .post(
        "https://eyedentity-socialmedia.herokuapp.com/post/create",{ header: {"token" : token }},data
      )
      .then((response) => {
        dispatch({
          type: "POST_ADD",
          payload: response.data,
        });
      });
  };
};
