import React, { useEffect } from "react";
import img1 from "../Img/img1.jpg";
import like from "../Img/like.svg";
import { connect } from "react-redux";
import "./post.css";
import { getData } from "../../actioncreators/Home";
import ShowComment from "./ShowComment";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faThumbsUp } from "@fortawesome/free-solid-svg-icons";

const Post = (props) => {
  const { data } = props;

  useEffect(() => {
    if (data && !data.length) {
      props.getData();
    }
  }, []);

  const showPost = data.map((item, index) => {
    console.log(item.tagPlace[0].namePlace);
    return (
      <div key={index}>
        <div class="card mt-3">
          <div class="card-header bg-light">
            {item.name.name}
          </div>
          <div class="card-body">
            <p class="card-text">
              {item.description}
            </p>
            <img
            style={{height}}
              className="card-img-top"
              src={`http://api.riyofirsan.com/${item.image}`}
              alt=""
            />
          </div>
        </div>
      </div>
    );
  });

  return <div className="container">{showPost}</div>;
};

const mapStateToProps = (state) => {
  return {
    data: state.homeUser.data,
  };
};

const mapDispatchToProps = {
  getData: getData,
};

export default connect(mapStateToProps, mapDispatchToProps)(Post);
