import React, { useState } from "react";
import "./Style.css";
import { connect } from "react-redux";
import Attach from "../Img/paperclip.svg";
import {add} from "../../actioncreators/Home"

const Add = (props) => {
  const [data, setData] = useState({
    description: "",
  });

  const handleAdd = () => {
    props.add(data);
  };

  const handleChange = (event) => {
    let { description } = event.currentTarget;
    setData({
      ...data,
      [description]: value,
    });
  };

  return (
    <div className="container">
      <div className="form-group">
        <textarea
          className="form-control mt-5"
          id="description"
          rows={6}

          placeholder="Type something...."
          onChange={handleChange}
        />
        <img src={Attach} style={{ width: "15px", marginBottom: "10px" }} />
      </div>

      <br />
      <button type="button" class="btn btn-info" onClick={handleAdd}>
        Submit
      </button>
    </div>
  );
};

const mapDispatchToProps = { add };

export default connect(null, mapDispatchToProps)(add);
