import axios from "axios";
let user

export const add = (data) => {
  return (dispatch) => {
    axios
      .post(
        "https://eyedentity-socialmedia.herokuapp.com/post/create",
        { headers: { "x-access-token" : `5eb51bbbd4277700171ebfad` } },
        data
      )
      .then((response) => {
        dispatch({
          type: "POST_ADD",
          payload: response.data,
        });
      });
  };
};
