import axios from "axios";
localStorage.setItem ('key',JSON.stringify(da))
let user = JSON.stringify()
export const add = (data) => {
  return (dispatch) => {
    axios
      .post(
        "https://eyedentity-socialmedia.herokuapp.com/post/create",data
      )
      .then((response) => {
        dispatch({
          type: "POST_ADD",
          payload: response.data,
        });
      });
  };
};
