import React, { useState, useEffect } from "react";
import axios from "axios";

import "./Add.css";
import { Card } from "react-bootstrap";

const CommentPost = (props) => {

    // const {data} = props

    const [comment,setComment] = useState([])

    const url = process.env.REACT_APP_API_URL

    useEffect(() =>{

        async function myComment() {
            const user = JSON.parse(localStorage.getItem("user"))
            const token = user.token
            const result = await axios.get(`${url}/comment/find/${props.data._id}`,{
            headers: { "token": token }
            }).catch(err => {
                window.alert("error",err)
            })
            setComment(result.data)
            console.log(result.data)
        }
        myComment()

    }, [])


    let getUser = comment.map((item,index) => {
        console.log(item.comment)
        return(

            <h6 key={item} data={index}>{item.comment.map((item,index) => <span key={index} data={item}>{item.userComment.name}</span>)}</h6>

        )

    })
    let getComment = comment.map((item,index) => {
        console.log(item.comment)
        return(
             <p key={item} data={index} >{item.comment.map((item,index) => <span key={index} data={item}>{item.commentText}</span>)}</p>
        )

    })



  return (
      <div className="card">
          <div className="card-header">
                this is Comment
          </div>
          <div className="card-body">
              <div className="card-title">
                  {getUser}
              </div>
              <div className="card-text">
                  {getComment}
              </div>
          </div>
      </div>
  )
};

export default CommentPost;
