import React, { useState, useEffect } from "react";
import { Formik } from "formik";
import "./Add.css";

import { connect } from "react-redux";
import { add } from "../../actioncreators/Home";
import { Form } from "react-bootstrap";
import Geocode from "react-geocode";
import Filter from "./Filter";
import Axios from "axios";

const Add = (props) => {
  const user = JSON.parse(localStorage.getItem("user"));
  const id = user.id;

  const [data, setData] = useState([]);
  const [tag, setTag] = useState([]);
  const [query, setQuery] = useState('');
  const [url, setUrl] = useState(
    'http://api.riyofirsan.com/users/findQuery?name=redux',
  );


  useEffect(() => {
    navigator.geolocation.getCurrentPosition(function (position) {
      console.log(position.coords.latitude);
      console.log(position.coords.longitude);
      Geocode.setApiKey("AIzaSyAVDqkzOARvGHvFnfaIYEiDBeIFaTjDDGI");
      Geocode.fromLatLng(
        position.coords.latitude,
        position.coords.longitude
      ).then((response) => {
        console.log(response.results[0].address_components[4].long_name);
        setData({
          lat: response.results[3].geometry.location.lat,
          lng: response.results[3].geometry.location.lng,
          city: response.results[3].formatted_address
        })
      })
    })

    const fetchData = async () => {
      const result = await aaxios(url)

      setTag(result.tag)

      fetchData()
    }

  },[url])

  const addTags = event =>{
    if(event.key == "Enter" && event.target.value !== ""){
      setTag([...tag,event.target.value])
      event.target.value = ""
    }
  }

  const removeTags = index =>{
    setTag([...tag.filter(tags=>tag.indexOf(tags) !== index)])
  }

  let city = data.city;
  // console.log(city)
  let lng = data.lng;
  // console.log(lng)
  let lath = data.lat;
  // console.log(lath)



  return (
    <Formik
      initialValues={{
        name: id,
        description: "",
        image: null,
        namePlace: city,
        long: lng,
        lat: lath,
        tag: ""
      }}
      onSubmit={(values) => {
        let formData = new FormData();

        formData.append("name", values.name);
        formData.append("description", values.description);
        formData.append("image", values.image);
        formData.append("namePlace", city);
        formData.append("long", lng);
        formData.append("lat", lath);
        formData.append("tag",values.tag)

        props.add(formData);
      }}
    >
      {(props) => (
        <Form onSubmit={props.handleSubmit}>
          <div className="container">
            <div className="form-group">
              <textarea
                className="form-control mt-5"
                id="description"
                name="description"
                rows={6}
                style={{ resize: "none" }}
                value={props.values.description}
                placeholder="Type something...."
                onChange={props.handleChange}
              />

              <div value={props.namePlace} onChange={props.handleChange} />
              {/* <div value={props.long} onChange={props.handleChange} />
              <div value={props.lat} onChange={props.handleChange} /> */}

return (
    <div className="tags-input">
        <ul id="tags">
            {tag.map((tags, index) => (
                <li key={index} className="tag">
                    <span className='tag-title'>{tags}</span>
                    <span className='tag-close-icon'
                        onClick={() => removeTags(index)}
                    >
                        x
                    </span>
                </li>
            ))}
        </ul>
        <input
            type="text"
            onChange={event => setQuery(event.target.value)}
            onKeyUp={event => event.key === "Enter" ? addTags(event) : null}
            placeholder="Press enter to add tags"
        />
    </div>

              <input
                type="file"
                className="form-control"
                id="image"
                name="image"
                onChange={(event) => {
                  props.setFieldValue("image", event.currentTarget.files[0]);
                }}
              />
            </div>

            <Filter />

            <button
              type="submit"
              className="btn btn-info"
              style={{ backgroundColor: "#8D7B65" }}
            >
              Submit
            </button>
          </div>
        </Form>
      )}
    </Formik>
  );
};

const mapDispatchToProps = { add: add };

export default connect(null, mapDispatchToProps)(Add);
