import React, { useEffect } from "react";
import { connect } from "react-redux";

import img1 from "../Img/img1.jpg";
import like from "../Img/like.svg";
import "./Post.css";

import { getData } from "./../../actioncreators/Home";

const Post = (props) => {
  useEffect(() => {
    if (!props.data.length) props.getData();
  }, );

  return (
    <div className="container mt-4">
      <div className="card" style={{ width: "auto" }}>
        <div className="row">
          <img src={img1} className="rounded-circle" alt="..." />
          <h4 className="card-title">User</h4>
        </div>
        <div className="card-body">
          <p className="card-text">{props.data.description}</p>
          <img src={like} style={{ width: "15px" }} />
        </div>
      </div>
    </div>
  );
};

const mapStateToProps = (state) => {
  return {
    data: state.students.data,
  };
};
const mapDispatchToProps = { getData : getData };

export default connect(mapStateToProps, mapDispatchToProps)(Post);
