import React, {useState,useEffect} from 'react'
import Navbar from '../Navbar'
import {Formik} from 'formik'
import axios from "axios"
import { connect } from "react-redux";
import './Addpage.css'
import {editProfile} from '../../actioncreators/Home'

const Add = (props) =>{

    const url = process.env.REACT_APP_API_URL

    const [data,setData] = useState([])
    const [profile,setProfile] = useState([])
    const [serverState, setServerState] = useState();
    const handleServerResponse = (ok, msg) => {
      setServerState({ok, msg});
    };





    return (
        <div className="yourStyle">
      <div className="card p-3 rounded" style={{ width: "30rem", backgroundColor : "white"}}>
        {/* <img
          src={Logo}
          className="mx-auto"
          alt="..."
          style={{ width: "55px" }}
        /> */}
        <h4 className="card-title mx-auto">want to change your style</h4>
        <Formik

            initialValues={{
                name : "",
                image : null,
                backGroundImage : null
            }}

            onSubmit={(values) =>{
                let formData = new FormData()

                formData.set("name",values.name)
                formData.append("image",values.image)
                formData.append("image",values.backGroundImage)

                props.editProfile(values)
            }}

        >{(props) =>(
            <Form onSubmit={props.handleSubmit}>
                <div className="form-group">
            <label>Email address</label>
            <input
              type="email"
              className="form-control"
              id="email"
              name="email"
            //   onChange={handleChange}
            //   value={data.email}
            />
          </div>
          <div className="form-group">
            <label>Password</label>
            <input
              type="password"
              className="form-control"
              id="password"
              name="password"
            //   onChange={handleChange}
            //   value={data.password}
            />
          </div>
          <div className="form-group form-check"></div>
          {/* <p className="text-center leading">
            Don't have an account ? <Link to="/register">Register</Link>
          </p> */}
            <button type="submit" className="btn text-light btn-block">
              Submit
            </button>
            </Form>
        )}

        </Formik>
      </div>
    </div>
    )

}

const mapDispatchToProps = {editProfile : editProfile}

export default connect(null, mapDispatchToProps)(Add)
