const initialState = {
    viaLogin: localStorage.getItem("x-access-token"),
    data: [],
};

const homeUser = (state = initialState, action) => {
  switch (action.type) {
    case "POST_ADD":
      return { ...state, data: [...state.data, action.payload] };
    default:
      return state;
  }
};

export default homeUser;
