import React ,{Component} from "react";
import {
  BrowserRouter as Router,
  Switch,
  Route,
  Redirect,
} from "react-router-dom";
import { connect } from "react-redux";

import {Provider} from "react-redux";
import store from "./store/store";

import jwt_decode from "jwt-decode";
import setAuthToken from "./utils";
import {setCurrentUser, logoutUser} from "./actioncreators/auth";

import Login from "./components/Login/Login";
import UserRegister from "./components/Login/Register";
import Home from "./components/Home";

// Check for token to keep user logged in
if (localStorage.jwtToken) {
  // Set auth token header auth
  const token = localStorage.jwtToken;
  setAuthToken(token);
  // Decode token and get user info and exp
  const decoded = jwt_decode(token);
  // Set user and isAuthenticated
  store.dispatch(setCurrentUser(decoded));
  // Check for expired token
  const currentTime = Date.now() / 1000; // to get in milliseconds
  if (decoded.exp < currentTime) {
      // Logout user
      store.dispatch(logoutUser());
      // Redirect to login
      // window.location.href = "./register";
  }
}
export default class App extends Component {
  render() {
      return (
          <Provider store={store}>
              <Router>
                <Switch>
                      <Route exact path="/" component={Home}/>
                      <Route exact path="/signup" component={UserRegister}/>
                      <Route exact path="/signin" component={Login}/>
                      {/* <Route exact path="/about" component={About}/> */}
                      {/* <Switch>
                          <PrivateRoute exact path="/details/detail/:id" component={ProductList}/>
                      </Switch> */}
                </Switch>
              </Router>
          </Provider>
      )
  }
}
