import React, {useState, useEffect} from 'react'
import {Formik, Form} from 'formik'
import "./Add.css";
import {connect} from "react-redux";
import {addComment} from "../../actioncreators/comment";
import {getComment} from "../../actioncreators/comment"
import {Modal,Button} from "react-bootstrap"
import axios from "axios"

const CommentPost = (props) => {

    const {data} = props

    useEffect(() => {
        const user = JSON.parse(localStorage.getItem("user"));
        const token = user.token;
        axios
          .get(`https://api.riyofirsan.com/comment/show`, {
            headers: { "token": token },
          })
          .then((response) => {

            console.log(response.data);

            // dispatch({
            //   type: "COMMENT_SHOW",
            //   payload: response.data,
            // });
          }).catch((err) => {
              console.log(err)
          });

    },[])



    const user = JSON.parse(localStorage.getItem("user"));
    const id = user.id;

    const showComment =data.map((item,index) => {
        console.log(item)
        return(
            <div key={item} data={index}>
            <div className="card mt-3 w-100" style={{ borderRadius: "10px" }}>
                <div className="card-header">
                    <div className="d-flex flex-row">
                        <div className="d-inline-flex flex-column">
                        <p className="lead font-weight-bold mb-0 ml-2">
                            {item.userComment}
                        </p>
                        </div>
                        <div className="card-body">
                            <p className="card-text">
                               {item.commentText}
                            </p>
                        </div>
                    </div>
                </div>
            </div>
        </div>
        )

    })

    return (
        <Modal
            {...props}
            size="lg"
            aria-labelledby="contained-modal-title-vcenter"
            centered>
            <Modal.Header closeButton>
                <Modal.Title id="contained-modal-title-vcenter">
                    {showComment}
                </Modal.Title>
            </Modal.Header>
            <Modal.Body>
            <div className="container"></div>;
            </Modal.Body>
            <Modal.Footer>
                <Formik
                    initialValues={{
                    targetPostId: props.data._id,
                    userId: id,
                    commentText: ''
                }}
                    onSubmit
                    ={(values, action) => {
                    if(values && !values){
                        window.alert("comment succes")
                        props.addcomment(values);
                        action.resetForm()
                    }

                }}>
                    {props => (
                        <Form onSubmit={props.handleSubmit}>
                            <div>
                                <div className="form-group">
                                    <textarea
                                        className="form-control"
                                        id="commentText"
                                        name="commentText"
                                        rows={2}
                                        style={{
                                        resize: "none"
                                    }}
                                        value={props.values.commentText}
                                        placeholder="Comment"
                                        onChange={props.handleChange}/>
                                </div>
                                <div className="row">
                                <button type="submit" className="btn text-light">
                                    Comment
                                </button>
                                <Button onClick={props.onHide}>Close</Button>
                                </div>
                            </div>
                        </Form>
                    )}
                </Formik>
            </Modal.Footer>
        </Modal>
    )

}

const mapStateToProps = (state) => {
    return {
        data: state.commentUser.data
    }
}

const mapDispatchToProps = {
    addComment: addComment,
    getComment: getComment,

};

export default connect(mapStateToProps, mapDispatchToProps)(CommentPost);
